/*Table structure for table `scenicway` */

DROP TABLE IF EXISTS `scenicway`;

CREATE TABLE `scenicway` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键',
  `way` varchar(100) DEFAULT NULL COMMENT '路线名称',
  `way_pinyin` varchar(100) DEFAULT NULL COMMENT '路线名称的拼音',
  `scenicday` int(11) DEFAULT NULL COMMENT '旅游天数',
  `description` varchar(200) DEFAULT NULL COMMENT '路线描述',
  `endpath` varchar(20) DEFAULT NULL COMMENT '终点',
  `startpath` varchar(20) DEFAULT NULL COMMENT '起点',
  `status` int(11) DEFAULT '0' COMMENT '线路状态',
  `floor_price` double DEFAULT NULL COMMENT '底价',
  `titleid` int(11) DEFAULT NULL COMMENT '主题表外键',
  `mealid` int(11) DEFAULT NULL COMMENT '套餐表外键',
  `attractionsId` int(11) DEFAULT NULL COMMENT '分类id',
  `scenicwayImage` varchar(100) DEFAULT NULL COMMENT '路线封面图片',
  `minimumGroupSize` int(4) DEFAULT NULL COMMENT '最少成团人数',
  `introduce` longtext COMMENT '路线介绍',
  PRIMARY KEY (`id`),
  KEY `mealid` (`mealid`),
  CONSTRAINT `scenicway_ibfk_1` FOREIGN KEY (`mealid`) REFERENCES `meal` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=45 DEFAULT CHARSET=utf8;

