  `sex` varchar(11) DEFAULT NULL COMMENT '性别',
  `addressId` int(11) DEFAULT NULL COMMENT '地址',
  `type` int(11) DEFAULT '0' COMMENT '状态(0.正常,1,删除,2.停用,3.注销)',
  `money` double DEFAULT '0' COMMENT '账户余额',
  `integral` int(11) DEFAULT '0' COMMENT '用户积分',
