mapper.select:
select 
from auth_teacher_v
where status = 1 and admin_id = #{aid}

mapper.update:
update auth_teacher_t
set status = 1, admin_id = #{aid}
where id=#{id}

update auth_teacher_t
set status = 2
where id=#{id}

update auth_teacher_t
set status = -1
where id=#{id}

update user_t
set teacher = 1
where id = #{uid}
